# encoding: utf-8
require 'icalendar'
require 'sinatra'

ICS_BLOB = Base64.decode64("")
ICS_BLOB.force_encoding("utf-8")

ICS_BLOB2 = Base64.decode64("")
ICS_BLOB2.force_encoding("utf-8")

get '/' do
    "hello eth"
end

get '/feed' do
    merge_events([single_event(ICS_BLOB), single_event(ICS_BLOB2)]).to_ical
end

def single_event(blob)
    cals = Icalendar::Calendar.parse(blob)
    cals.first.events.first
end

def merge_events(events)
    cal = Icalendar::Calendar.new
    events.each {|event| cal.add_event(event)}
    cal
end